sender_address = "0x875FAfaDD195f29485701AF3Cd20CaA67aceb646" #'0xdCc20789098843025CD54d0799714545d894D85e'
#Contract Address of Token we want to buy
#web3.toChecksumAddress("0x6615a63c260be84974166a5eddff223ce292cf3d")
tokenToBuy = web3.toChecksumAddress(input("Enter TokenAddress: "))
#wbnb contract
spend = web3.toChecksumAddress("0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c")

def get_balance(ETH_address):
    balance = web3.eth.get_balance(ETH_address)
    humanReadable = web3.fromWei(balance, 'ether')
    return humanReadable


print(get_balance(sender_address))

# def get_nonce(ETH_address):
#     return web3.eth.get_transaction_count(ETH_address)


def buy_token():
    # Setup the PancakeSwap contract
    contract = web3.eth.contract(address=panRouterContractAddress, abi=panabi)
    start = time.time()
    txn_body={
        'from': sender_address,
        'value': web3.toWei(0.001, 'ether'),  # This is the Token(BNB) amount you want to Swap from
        'gas': 250000,
        'gasPrice': web3.toWei('5', 'gwei'),
        # 'nonce': get_nonce(sender_address),
        'nonce': web3.eth.get_transaction_count(sender_address)
    }
    pancakeswap2_txn=contract.functions.swapExactETHForTokens(10000000000,[spend,tokenToBuy],(int(start) + 10000)).buildTransaction(txn_body)
    signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key=os.environ["KEY"])
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
    print(web3.toHex(tx_token))
    

      
